import React, { useEffect, useState } from "react";
import { useStripe, useElements, CardElement } from "@stripe/react-stripe-js";
import { useNavigate } from "react-router-dom";
import "./payment.css";

const Payment = ({ amount = 500 }) => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const [method, setMethod] = useState("card");
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("auth-token");
    if (!token) {
      alert("Please login to continue");
      navigate("/login");
    }
  }, [navigate]);

  /* ===== CARD PAYMENT ===== */
  const payByCard = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg("");

    try {
      const token = localStorage.getItem("auth-token");

      const res = await fetch("http://localhost:7000/create-payment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": token,
        },
        body: JSON.stringify({ amount }),
      });

      const { clientSecret } = await res.json();

      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
        },
      });

      if (result.error) {
        setMsg(result.error.message);
      } else {
        await saveOrder("succeeded", result.paymentIntent.id);
      }
    } catch {
      setMsg("Payment failed");
    }
    setLoading(false);
  };

  /* ===== COD ===== */
  const payByCOD = async () => {
    setLoading(true);
    await saveOrder("cod", "COD_" + Date.now());
    setLoading(false);
  };

  /* ===== SAVE ORDER ===== */
  const saveOrder = async (status, paymentId) => {
    const token = localStorage.getItem("auth-token");

    await fetch("http://localhost:7000/save-order", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token": token,
      },
      body: JSON.stringify({
        cartItems: [],
        totalAmount: amount,
        paymentId,
        status,
        address: {},
      }),
    });

    alert("Order Placed Successfully");
    navigate("/orders");
  };

  return (
    <div className="pay-page">
      <div className="pay-box">
        <h2>Pay â‚¹{amount}</h2>

        <div className="methods">
          <button onClick={() => setMethod("card")} className={method==="card"?"active":""}>Card</button>
          <button onClick={() => setMethod("cod")} className={method==="cod"?"active":""}>COD</button>
        </div>

        {method === "card" && (
          <form onSubmit={payByCard}>
            <CardElement className="card-el" />
            <button disabled={loading}>
              {loading ? "Processing..." : "Pay Now"}
            </button>
          </form>
        )}

        {method === "cod" && (
          <button onClick={payByCOD} disabled={loading}>
            Place COD Order
          </button>
        )}

        {msg && <p className="error">{msg}</p>}
      </div>
    </div>
  );
};

export default Payment;
