import React, { useState, useEffect } from "react";
import { QRCodeSVG } from "qrcode.react";
import axios from "axios";
import upiQR from '../../Component/Assets/qr1.jpeg'
const UPIPayment = ({ amount }) => {
  const [paymentIntentId, setPaymentIntentId] = useState("");
  const [upiQR, setUpiQR] = useState("");

  const handleUPIPayment = async () => {
    try {
      const token = localStorage.getItem("auth-token");

      const res = await axios.post(
        "http://localhost:7000/create-payment",
        { amount },
        { headers: { "auth-token": token } }
      );

      const { paymentIntentId } = res.data; // <-- use paymentIntentId from backend
      setPaymentIntentId(paymentIntentId);

      const qrString = `upi://pay?pa=methaniyajanvi636@okaxis&pn=Janvi&am=${amount}&cu=INR&tr=${paymentIntentId}`;
      setUpiQR(qrString);
    } catch (err) {
      console.log("Error creating payment:", err);
    }
  };

  useEffect(() => {
    if (!paymentIntentId) return;

    const interval = setInterval(async () => {
      try {
        const token = localStorage.getItem("auth-token");
        const res = await axios.post(
          "http://localhost:7000/check-payment",
          { paymentIntentId },
          { headers: { "auth-token": token } }
        );

        if (res.data.status === "succeeded") {
          alert("Payment Successful!");
          clearInterval(interval);
        }
      } catch (err) {
        console.log(err);
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [paymentIntentId]);

  return (
    <div style={{ textAlign: "center", marginTop: 50 }}>
      <h2>Pay via UPI</h2>
      {!upiQR && (
        <button
          onClick={handleUPIPayment}
          style={{ padding: "10px 20px", fontSize: 16 }}
        >
          Generate UPI QR
        </button>
      )}
     {upiQR && (
  <div className="qr-area">
    <p>Scan this QR with your UPI app to pay â‚¹{amount}</p>
    <QRCodeSVG id="upi-qr" value={upiQR} size={250} />
    <button
      onClick={() => {
        const svg = document.getElementById("upi-qr");
        const svgData = new XMLSerializer().serializeToString(svg);
        const svgBlob = new Blob([svgData], { type: "image/svg+xml;charset=utf-8" });
        const url = URL.createObjectURL(svgBlob);
        const link = document.createElement("a");
        link.href = url;
        link.download = "UPI_QR.svg";
        link.click();
      }}
      style={{ marginTop: 10 }}
    >
      Download QR
    </button>
  </div>
)}

    </div>
  );
};

export default UPIPayment;
