// src/pages/Payment.jsx
import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import UPIPayment from "./UPIPayment";
import "./payment.css";

const Payment = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Safely get state from Checkout page
  const state = location.state || {};
  const cartProducts = state.cartProducts || [];
  const amount = state.amount || 0;
  const address = state.address || {};

  const [method, setMethod] = useState("cod");
  const [showUPI, setShowUPI] = useState(false);
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  // Redirect if no cartProducts
  useEffect(() => {
    if (cartProducts.length === 0 || amount <= 0) {
      alert("No products selected, redirecting to Checkout");
      navigate("/checkout");
    }
  }, [cartProducts, amount, navigate]);

  /* ===== COD ===== */
  const payByCOD = async () => {
    setLoading(true);
    const token = localStorage.getItem("auth-token");

    try {
      await fetch("http://localhost:7000/save-order", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": token,
        },
        body: JSON.stringify({
          cartItems: cartProducts,
          totalAmount: amount,
          paymentId: "COD_" + Date.now(),
          status: "cod",
          address,
        }),
      });

      alert("Order Placed Successfully (COD)");
      navigate("/orders");
    } catch (err) {
      setMsg("Failed to place COD order");
    }
    setLoading(false);
  };

  /* ===== UPI ===== */
  const openUPI = () => {
    setMethod("upi");
    setShowUPI(true);
  };

  return (
    <div className="payment-page">
      <div className="payment-card">
        {/* LEFT PANEL */}
        <div className="pay-left">
          <h2>Payment Details</h2>

          <div className="address-box">
            <strong>Shipping To:</strong>
            <div>{address.name}</div>
            <div>{address.phone}</div>
            <div>
              {address.line1}, {address.city}, {address.state} - {address.pincode}
            </div>
          </div>

          {!showUPI && (
            <div className="payment-options">
              <div
                className={`opt ${method === "cod" ? "active" : ""}`}
                onClick={() => {
                  setMethod("cod");
                  setShowUPI(false);
                }}
              >
                COD
              </div>
              <div
                className={`opt ${method === "upi" ? "active" : ""}`}
                onClick={openUPI}
              >
                UPI
              </div>
            </div>
          )}

          {method === "cod" && !showUPI && (
            <button className="btn" onClick={payByCOD} disabled={loading}>
              {loading ? "Processing..." : `Place COD Order — ₹${amount}`}
            </button>
          )}

          {showUPI && <UPIPayment amount={amount} />}

          {msg && <div className="msg error">{msg}</div>}
        </div>

        {/* RIGHT PANEL */}
        <div className="pay-right">
          <h3>Order Summary</h3>
          <div className="summary-scroll">
            {cartProducts.map((item) => (
              <div className="summary-line" key={item.id}>
                <img src={item.image} alt={item.name} />
                <div className="meta">
                  <strong>{item.name}</strong>
                  <span>
                    Qty: {item.quantity}
                    {item.size ? ` • ${item.size}` : ""}
                  </span>
                </div>
                <div>₹{item.new_price * item.quantity}</div>
              </div>
            ))}
          </div>

          <div className="summary-bottom">
            <div className="row">
              <span>Subtotal</span>
              <span>₹{amount}</span>
            </div>
            <div className="total-row">
              <span>Total</span>
              <span>₹{amount}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Payment;
