import React, { useEffect, useState } from "react";

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const [tracking, setTracking] = useState({});
  const [openOrder, setOpenOrder] = useState(null);

  useEffect(() => {
    fetch("http://localhost:7000/my-orders", {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token")
      }
    })
      .then(res => res.json())
      .then(data => setOrders(data));
  }, []);

  const loadTracking = async (orderId) => {
    const res = await fetch(`http://localhost:7000/order-entries/${orderId}`);
    const data = await res.json();
    setTracking(prev => ({ ...prev, [orderId]: data }));
  };

  return (
    <div className="myorders">
      <h2>ðŸ“¦ My Orders</h2>

      {orders.map(order => (
        <div key={order._id} className="order-box">
          <p><b>Order ID:</b> {order._id.slice(-8)}</p>
          <p><b>Total:</b> â‚¹{order.totalAmount}</p>

          <button
            onClick={() => {
              setOpenOrder(openOrder === order._id ? null : order._id);
              loadTracking(order._id);
            }}
          >
            {openOrder === order._id ? "Hide Tracking" : "Track Order"}
          </button>

          {openOrder === order._id && (
            <div className="tracking">
              {tracking[order._id]?.map((t, i) => (
                <div key={i} className="track-item">
                  <p>âœ… {t.description}</p>
                  {t.notes && <small>Note: {t.notes}</small>}
                  <small>{new Date(t.timestamp).toLocaleString()}</small>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default MyOrders;
