import React, { useState, useEffect } from "react";
import { useStripe, useElements, CardElement } from "@stripe/react-stripe-js";
import { useLocation, useNavigate } from "react-router-dom";
import UPIPayment from "../../Component/payment/UPIPayment";
import "./payment.css";

const Payment = () => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const location = useLocation();

  // Data from Checkout
  const { amount, cartProducts, address } = location.state || {};
  const [method, setMethod] = useState("card");
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");
  const [showUPI, setShowUPI] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("auth-token");
    if (!token || !amount || !cartProducts) {
      alert("Please login and select products to continue");
      navigate("/checkout");
    }
  }, [amount, cartProducts, navigate]);

  /* ===== CARD PAYMENT ===== */
  const payByCard = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg("");
    try {
      const token = localStorage.getItem("auth-token");

      const res = await fetch("http://localhost:7000/create-payment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": token,
        },
        body: JSON.stringify({ amount }),
      });

      const { clientSecret } = await res.json();

      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: elements.getElement(CardElement) },
      });

      if (result.error) {
        setMsg(result.error.message);
      } else {
        await saveOrder("succeeded", result.paymentIntent.id);
      }
    } catch {
      setMsg("Payment failed");
    }
    setLoading(false);
  };

  /* ===== COD ===== */
  const payByCOD = async () => {
    setLoading(true);
    await saveOrder("cod", "COD_" + Date.now());
    setLoading(false);
  };

  /* ===== UPI ===== */
  const payByUPI = () => {
    setShowUPI(true);
  };

  /* ===== SAVE ORDER ===== */
  const saveOrder = async (status, paymentId) => {
    const token = localStorage.getItem("auth-token");

    await fetch("http://localhost:7000/save-order", {
      method: "POST",
      headers: { "Content-Type": "application/json", "auth-token": token },
      body: JSON.stringify({
        cartItems: cartProducts,
        totalAmount: amount,
        paymentId,
        status,
        address,
      }),
    });

    alert("Order Placed Successfully");
    navigate("/orders");
  };

  return (
    <div className="pay-page">
      <div className="pay-box">
        <h2>Pay ₹{amount}</h2>

        {!showUPI && (
          <div className="methods">
            <button onClick={() => setMethod("card")} className={method === "card" ? "active" : ""}>Card</button>
            <button onClick={() => setMethod("cod")} className={method === "cod" ? "active" : ""}>COD</button>
            <button onClick={payByUPI} className={method === "upi" ? "active" : ""}>UPI</button>
          </div>
        )}

        {method === "card" && !showUPI && (
          <form onSubmit={payByCard}>
            <CardElement className="card-el" />
            <button disabled={loading}>{loading ? "Processing..." : "Pay Now"}</button>
          </form>
        )}

        {method === "cod" && !showUPI && (
          <button onClick={payByCOD} disabled={loading}>Place COD Order</button>
        )}

        {showUPI && <UPIPayment amount={amount} />}

        {msg && <p className="error">{msg}</p>}
      </div>

      {/* Optional: show order summary */}
      <div className="summary-box">
        <h3>Order Summary</h3>
        {cartProducts.map((item) => (
          <div key={item.id} className="summary-item">
            <img src={item.image} alt={item.name} />
            <div className="meta">
              <div>{item.name}</div>
              <div>Qty: {item.quantity} {item.size ? ` • ${item.size}` : ""}</div>
            </div>
            <div>₹{item.new_price * item.quantity}</div>
          </div>
        ))}
        <div className="summary-total">
          <strong>Total: ₹{amount}</strong>
        </div>
      </div>
    </div>
  );
};

export default Payment;
