import React,{useEffect,useState} from "react";
import axios from "axios";
import './myorder.css';

const MyOrder = ({token})=>{
  const [orders,setOrders]=useState([]);
  const [loading,setLoading]=useState(true);
  const [expandedOrder,setExpandedOrder]=useState(null);
  const [orderEntries,setOrderEntries]=useState({});

  useEffect(()=>{
    fetchOrders();
  },[]);

  const fetchOrders=async()=>{
    try{
      const res = await axios.get("http://localhost:7000/my-orders",{headers:{"auth-token":token}});
      setOrders(res.data);
      setLoading(false);
    }catch(err){
      console.error(err);
      setLoading(false);
    }
  }

  const fetchOrderEntries=async(orderId)=>{
    try{
      const res = await axios.get(`http://localhost:7000/order-entries/${orderId}`);
      setOrderEntries(prev=>({...prev,[orderId]:res.data}));
    }catch(err){console.error(err);}
  }

  const formatDate=dateStr=>new Date(dateStr).toLocaleString("en-IN",{year:"numeric",month:"short",day:"numeric",hour:"2-digit",minute:"2-digit"});
  const getStatusColor=status=>{
    switch(status){
      case "succeeded": return "#31a049";
      case "pending": return "#ff9f00";
      case "cod": return "#2874f0";
      case "failed": return "#d32f2f";
      default:return "#666";
    }
  }

  if(loading) return <div>Loading...</div>;
  if(orders.length===0) return <div>No orders found</div>;

  return(
    <div className="myorders-container">
      <h2>üõí My Orders</h2>
      {orders.map(order=>(
        <div key={order._id} className="order-card">
          <div className="order-header">
            <span>Order ID: {order._id.slice(-8).toUpperCase()}</span>
            <span>Date: {formatDate(order.date)}</span>
            <span style={{backgroundColor:getStatusColor(order.status),color:"#fff",padding:"2px 8px",borderRadius:"6px",fontWeight:"bold"}}>
              {order.status==="succeeded"?"‚úì Paid":order.status==="cod"?"üí≥ COD":order.status==="pending"?"‚è≥ Pending":"‚úó Failed"}
            </span>
          </div>

          <div className="order-items">
            {order.cartItems?.map((item,idx)=>(
              <div key={idx} style={{display:"flex",margin:"5px 0"}}>
                {item.image&&<img src={item.image} alt={item.name} width={60} height={60} style={{marginRight:"10px",borderRadius:"6px"}}/>}
                <div>
                  <p>{item.name}</p>
                  <p>Qty: {item.quantity}</p>
                  <p>Price: ‚Çπ{item.new_price}</p>
                </div>
              </div>
            ))}
          </div>

          <button onClick={()=>{
            if(expandedOrder===order._id) setExpandedOrder(null);
            else {setExpandedOrder(order._id); if(!orderEntries[order._id]) fetchOrderEntries(order._id);}
          }} style={{margin:"10px 0",padding:"5px 10px",borderRadius:"6px",cursor:"pointer",backgroundColor:"#2874f0",color:"#fff",border:"none"}}>
            {expandedOrder===order._id?"‚ñ≤ Hide Tracking":"‚ñº Show Tracking"}
          </button>

          {expandedOrder===order._id && orderEntries[order._id] && (
            <div className="tracking-timeline">
              {orderEntries[order._id].map((entry,idx)=>(
                <div key={idx} style={{display:"flex",marginBottom:"8px",alignItems:"flex-start"}}>
                  <div style={{width:"10px",height:"10px",borderRadius:"50%",backgroundColor:"#31a049",marginRight:"8px",marginTop:"6px"}}></div>
                  <div>
                    <p><strong>{entry.action}</strong> - {entry.description}</p>
                    {entry.notes && <p>Note: {entry.notes}</p>}
                    <p style={{fontSize:"0.8rem",color:"#555"}}>{formatDate(entry.timestamp)}</p>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  )
}

export default MyOrder;
