import React, { useContext, useState } from "react";
import "./productdisplay.css";
import star_dull from "../Assets/star_dull_icon.png";
import { ShopContext } from "../../Context/Shopcontext";
import { useNavigate } from "react-router-dom";

const ProductDisplay = ({ product }) => {
  const { addToCartWithSize } = useContext(ShopContext);
  const navigate = useNavigate();

  const [selectedSize, setSelectedSize] = useState("");
  const [quantity, setQuantity] = useState(1);

  /* ================= ADD TO CART ================= */
  const handleAddToCart = () => {
    if (!selectedSize) {
      alert("Please select a size");
      return;
    }

    for (let i = 0; i < quantity; i++) {
      addToCartWithSize(product.id, selectedSize);
    }

    alert("Product added to cart!");
    setSelectedSize("");
    setQuantity(1);
  };

  /* ================= BUY NOW ================= */
  const handleBuyNow = () => {
    const token = localStorage.getItem("auth-token");

    if (!token) {
      navigate("/login");
      return;
    }

    if (!selectedSize) {
      alert("Please select a size");
      return;
    }

    // Add product to cart first
    for (let i = 0; i < quantity; i++) {
      addToCartWithSize(product.id, selectedSize);
    }

    navigate("/checkout");
  };

  const discount = Math.round(
    ((product.old_price - product.new_price) / product.old_price) * 100
  );

  return (
    <div className="productdisplay">
      <div className="productdisplay-left">
        <div className="productdisplay-img-container">
          <img
            className="productdisplay-main-img"
            src={product.image || product.img}
            alt={product.name}
          />
          {discount > 0 && <div className="discount-tag">{discount}% OFF</div>}
        </div>
      </div>

      <div className="productdisplay-right">
        <h1>{product.name}</h1>
        <p className="product-category">{product.category}</p>

        <div className="productdisplay-right-star">
          {[...Array(5)].map((_, i) => (
            <img key={i} src={star_dull} alt="star" />
          ))}
          <span>(122 reviews)</span>
        </div>

        <div className="productdisplay-right-prices">
          <span className="old-price">â‚¹{product.old_price}</span>
          <span className="new-price">â‚¹{product.new_price}</span>
          {discount > 0 && <span className="discount">{discount}% OFF</span>}
        </div>

        {/* SIZE */}
        <div className="size">
          <h3>Select Size</h3>
          <div className="size-options">
            {["S", "M", "L", "XL", "XXL"].map((size) => (
              <div
                key={size}
                className={`size-option ${
                  selectedSize === size ? "active" : ""
                }`}
                onClick={() => setSelectedSize(size)}
              >
                {size}
              </div>
            ))}
          </div>
        </div>

        {/* QUANTITY */}
        <div className="quantity-section">
          <h3>Quantity</h3>
          <div className="quantity-control">
            <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>
              âˆ’
            </button>
            <input type="number" value={quantity} readOnly />
            <button onClick={() => setQuantity(quantity + 1)}>+</button>
          </div>
        </div>

        {/* ACTION BUTTONS */}
        <div className="action-buttons">
          <button className="add-to-cart-btn" onClick={handleAddToCart}>
            ðŸ›’ Add To Cart
          </button>

          <button className="buy-now-btn" onClick={handleBuyNow}>
            âš¡ Buy Now
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDisplay;
