import React, { createContext, useState, useEffect } from "react";

export const ShopContext = createContext(null);

/* ================= DEFAULT CART ================= */
const getDefaultCart = () => {
  const cart = {};
  for (let i = 1; i <= 300; i++) {
    cart[i] = { qty: 0, size: "" };
  }
  return cart;
};

const ShopContextProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState(getDefaultCart());

  /* ================= FETCH PRODUCTS ================= */
  useEffect(() => {
    fetch("http://localhost:7000/allproducts")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.log("Products Error:", err));
  }, []);

  /* ================= FETCH CART FROM BACKEND ================= */
  useEffect(() => {
    if (localStorage.getItem("auth-token")) {
      fetch("http://localhost:7000/getcart", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("auth-token"),
        },
      })
        .then((res) => res.json())
        .then((data) => {
          const formattedCart = getDefaultCart();

          for (let id in data) {
            formattedCart[id] = {
              qty: data[id],
              size: "",
            };
          }

          setCartItems(formattedCart);
        })
        .catch((err) => console.log("Cart Error:", err));
    }
  }, []);

  /* ================= ADD TO CART ================= */
  const addToCart = (itemId) => {
    setCartItems((prev) => ({
      ...prev,
      [itemId]: {
        qty: prev[itemId].qty + 1,
        size: prev[itemId].size,
      },
    }));

    if (localStorage.getItem("auth-token")) {
      fetch("http://localhost:7000/addtocart", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("auth-token"),
        },
        body: JSON.stringify({ itemId }),
      });
    }
  };

  /* ================= REMOVE FROM CART ================= */
  const removeFromCart = (itemId) => {
    setCartItems((prev) => ({
      ...prev,
      [itemId]: {
        qty: Math.max(prev[itemId].qty - 1, 0),
        size: prev[itemId].size,
      },
    }));
  };

  /* ================= CLEAR CART ================= */
  const clearCart = () => {
    setCartItems(getDefaultCart());
  };

  /* ================= TOTAL ITEMS (NAVBAR) ================= */
  const getTotalItems = () => {
    let total = 0;
    for (let id in cartItems) {
      total += cartItems[id]?.qty || 0;
    }
    return total;
  };

  /* ================= TOTAL AMOUNT ================= */
  const getTotalCartAmount = () => {
    let total = 0;

    for (let id in cartItems) {
      if (cartItems[id].qty > 0) {
        const product = products.find(
          (p) => p.id === Number(id)
        );
        if (product) {
          total += product.new_price * cartItems[id].qty;
        }
      }
    }

    return total;
  };

  /* ================= CONTEXT VALUE ================= */
  const contextValue = {
    all_product: products,
    cartItems,
    addToCart,
    removeFromCart,
    clearCart,
    getTotalItems,
    getTotalCartAmount,
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
