import React, { useState, useEffect } from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { QRCodeSVG } from "qrcode.react";
import { useNavigate } from "react-router-dom";

const Payment = ({ amount = 500 }) => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const [method, setMethod] = useState("card");
  const [upiQR, setUpiQR] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!localStorage.getItem("auth-token")) {
      alert("Please login");
      navigate("/login");
    }
  }, []);

  /* ===== CARD PAYMENT ===== */
  const payByCard = async (e) => {
    e.preventDefault();
    setLoading(true);

    const res = await fetch("http://localhost:7000/create-payment", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("auth-token"),
      },
      body: JSON.stringify({ amount }),
    });

    const { clientSecret, paymentIntentId } = await res.json();

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (result.error) {
      alert(result.error.message);
    } else {
      saveOrder("succeeded", paymentIntentId);
    }
    setLoading(false);
  };

  /* ===== COD ===== */
  const payByCOD = () => {
    saveOrder("cod", "COD_" + Date.now());
  };

  /* ===== UPI ===== */
  const generateUPI = () => {
    const upiString = `upi://pay?pa=merchant@upi&pn=Ecommerce&am=${amount}&cu=INR`;
    setUpiQR(upiString);
  };

  /* ===== SAVE ORDER ===== */
  const saveOrder = async (status, paymentId) => {
    await fetch("http://localhost:7000/save-order", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("auth-token"),
      },
      body: JSON.stringify({
        cartItems: [],
        totalAmount: amount,
        paymentId,
        status,
        address: {},
      }),
    });

    alert("Order Placed Successfully");
    navigate("/orders");
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h2>Pay â‚¹{amount}</h2>

      <button onClick={() => setMethod("card")}>Card</button>
      <button onClick={() => setMethod("upi")}>UPI</button>
      <button onClick={() => setMethod("cod")}>COD</button>

      {method === "card" && (
        <form onSubmit={payByCard}>
          <CardElement />
          <button disabled={loading}>Pay Now</button>
        </form>
      )}

      {method === "upi" && (
        <>
          {!upiQR && <button onClick={generateUPI}>Generate UPI QR</button>}
          {upiQR && <QRCodeSVG value={upiQR} size={250} />}
          <p>After payment, click confirm</p>
          <button onClick={() => saveOrder("upi", "UPI_" + Date.now())}>
            Confirm Payment
          </button>
        </>
      )}

      {method === "cod" && (
        <button onClick={payByCOD}>Place COD Order</button>
      )}
    </div>
  );
};

export default Payment;
